import {EventEmitter} from 'events'
import {stringify as qsStringify} from 'querystring'
import io from 'socket.io-client'
import {version} from '../../../package.json'
import passEvent from '../../lib/pass-event'
import Replicator from './replicator'

/**
 * Handles connection with the backend.
 *
 * Responsible for establishing a WebSocket (socket.io) connection with
 * the backend and handling its requests.
 *
 * It also instantiates {@link Replicator} instances for data and log
 * replication. The stateChange events from these are forwarded as:
 * "replicator:data:state" and "replicator:log:state".
 */
export default class Backend extends EventEmitter {
	/**
	 * Constructor.
	 * @param {String} name Station name.
	 * @param {Bunyan} logger Logger instance.
	 * @param {PouchDB} dataDB Packet database.
	 * @param {PouchDB} logDB Log entries database.
	 */
	constructor(name, logger, dataDB, logDB) {
		super()

		/**
		 * Station name.
		 * @type {String}
		 */
		this.name = name

		/**
		 * Current socket connection with the backend.
		 * @type {!IO}
		 */
		this._socket = undefined

		/**
		 * Current socket state.
		 * @type {String}
		 */
		this._state = 'inactive'

		/**
		 * Logger instance.
		 * @type {Bunyan}
		 */
		this._log = logger

		/**
		 * {@link Replicator} instance for dataDB.
		 * @type {Replicator}
		 */
		this.dataReplicator = new Replicator(
			this._log.child({childId: 'backend.replicator:data'}),
			dataDB
		)

		passEvent(this.dataReplicator, this, 'state', 'replicator:data:state')

		/**
		 * {@link Replicator} instance for logDB.
		 * @type {Replicator}
		 */
		this.logReplicator = new Replicator(
			this._log.child({childId: 'backend.replicator:log'}),
			logDB
		)

		passEvent(this.logReplicator, this, 'state', 'replicator:log:state')

		this._log.info('backend.construct')
	}

	/**
	 * Connects to backend at [url].
	 * @param {String} url Backend URL.
	 * @emits state(socketState): socket connection/disconnection.
	 */
	connect(url) {
		this._log.info('connect to', url)

		this._socket = io(url, {
			query: qsStringify({
				name: this.name
				// TODO: include auth info
			})
		})

		// Return env information
		this._socket.on('info', cb => cb({version, name: this.name}))

		// Keep track of state
		this._socket.on('reconnecting', attempt => this._updateState((this._state === 'inactive' ? 'connecting' : 'reconnecting'), {attempt}))
		this._socket.on('reconnect_error', () => this._updateState(this._state === 'connecting' ? 'connect_error' : 'reconnect_error'))
		this._socket.on('connect', () => this._updateState('connect'))
		this._socket.on('disconnected', () => this._updateState('disconnect'))

		// Respond to replication requests
		this._socket.on('replicate', (dataDbUrl, logDbUrl, user, pass) => {
			this.dataReplicator.replicate(dataDbUrl, user, pass)
			this.logReplicator.replicate(logDbUrl, user, pass)
		})
	}

	/**
	 * Disconnects from the backend. Stops the socket.
	 * The replicators will keep running until a new connection makes them
	 * connect to a different database.
	 * @emits state(socketState): socket connection/disconnection.
	 */
	disconnect() {
		this._log.info('backend.disconnect')

		// TODO: warn disconnection through socket (soft disconnection)

		// Disconnect socket to stop reconnection logic
		this._socket.disconnect()

		// Finally remove the socket reference here
		this._socket = null

		// Stop the replicators
		// The operator may be experiencing issues and prefer to keep them off
		this.dataReplicator.stop()
		this.logReplicator.stop()

		this._updateState('inactive')
	}

	/**
	 * Triggers {@link Replicator#cleanup} on the data and log replicators.
	 * @emits state('cleanup')
	 * @returns {Promise}
	 */
	async cleanup() {
		this._log.info('backend.cleanup')
		this._updateState('cleanup')

		// Cleanup replicators
		await Promise.all([
			this.dataReplicator.cleanup(),
			this.logReplicator.cleanup()
		])

		// TODO: soft-disconnect (maybe?)
	}

	/**
	 * Updates the socket state.
	 * @protected
	 * @param {String} state New Backend state.
	 * @param {Object} [extraData] Extra data related to the new state (error, no. of connection attempts...) to be included in logs.
	 * @emits state(socketState): socket connection/disconnection.
	 */
	_updateState(state, extraData) {
		this._log.info('updateState', state, extraData)
		this._state = state
		this.emit('state', state)
	}
}

import {EventEmitter} from 'events'
import ExponentialBackoff from '../../lib/backoff-wrapper'
import {getRemoteDB} from '../../lib/db'

/**
 * Handles replication between a local and remote PouchDB database.
 * Keeps track of state, uses an {@link ExponentialBackoff} to prevent
 * connection saturation, serving as a wrapper over PouchDB replication.
 *
 * The source database is set once in the constructor and remains unchanged
 * and unchangeable during operation.
 */
export default class Replicator extends EventEmitter {

	/**
	 * @param {Logger} logger Bunyan logger instance.
	 * @param {PouchDB} sourceDB Database to be replicated.
	 */
	constructor(logger, sourceDB) {
		super()

		/**
		 * Logger instance.
		 * @type {Bunyan}
		 */
		this._log = logger

		/**
		 * Replicator status.
		 * @type {String}
		 */
		this._state = 'inactive'

		/**
		 * Source database.
		 * @type {PouchDB}
		 */
		this._sourceDB = sourceDB

		/**
		 * Target database.
		 * @type {!PouchDB}
		 */
		this._targetDB = undefined

		/**
		 * ExponentialBackoff instance.
		 * Keeps track of the replication backoff.
		 * @type {ExponentialBackoff}
		 */
		this._backoff = new ExponentialBackoff()

		/**
		 * PouchDB replication instance/ID.
		 * @type {!PouchDBReplicationID}
		 */
		this._replication = undefined

		this._backoff.on('retry', retry => setImmediate(() => this._ensureReplication(retry)))
		this._backoff.on('backoff', (retry, delay) => {
			// Cancels current replicator if there is one (no need in keeping an errored replicator)
			this._cancelCurrentReplicator()

			// Don't signal we're connecting if we're inactive
			if (this._state === 'inactive' || this._state === 'cleanup') {
				return
			}

			this._log.debug('Backing off from replication', {retry, delay})
			this._updateState('idle')
		})
	}

	/**
	 * Stops the replication process.
	 * @emits state('inactive')
	 */
	stop() {
		this._log.info('Stopping replication')
		this._updateState('inactive')

		this._cancelCurrentReplicator()
		this._backoff.reset()
	}

	_cancelCurrentReplicator() {
		if (this._replication) {
			// Prevent the replicator from triggering the backoff
			this._replication.removeAllListeners()

			// Get rid of it
			this._replication.cancel()
			this._replication = undefined
		}
	}

	/**
	 * Stops current replication and attempts last-minute replication.
	 * Last-minute replication is done in "one-shot" mode: replicates whatever data it has
	 * and immediately stops without listening for changes in the local database.
	 * @emits state('cleanup')
	 * @returns {Promise}
	 */
	async cleanup() {
		// Before doing anything, stop!
		this.stop()

		this._log.info('cleanup')
		this._updateState('cleanup')

		let ok = false
		try {
			// Before going away attempt one last replication to get the remaining data out
			ok = (await this._createReplicator({live: false, retry: true})).ok
		} catch (err) {
			this._log.warn('Error in last-minute replication', {err})
		}

		if (ok) {
			this._log.info('Last minute replication successful')
		} else {
			this._log.warn('Last minute replication failed')
		}
	}

	/**
	 * Starts replication to a remote database.
	 * @param {String} dbName Name/URL of the remote database.
	 * @param {String} [username] Database username, if applicable.
	 * @param {String} [password] Database password, if applicable.
	 * @emits state(state): the state of the replicator.
	 * @returns {Promise} resolved when the replication connection succeeds for the first time.
	 */
	replicate(dbUrl, username, password) {
		// Don't try replicating when we're going away
		if (this._state === 'cleanup') {
			return Promise.resolve()
		}

		// Stop any already running replication
		this.stop()

		this._log.info('Live replication triggered', {dbUrl, username})
		this._updateState('idle')

		// Create the DB object here to avoid memory leaks
		this._targetDB = getRemoteDB(dbUrl, username, password)

		// Kick the process into action
		return new Promise(resolve => {
			this._backoff.once('success', resolve)
			this._backoff.backoff()
		})
	}

	/**
	 * Restarts the replication process when unintentionally stopped.
	 * @param {Number} retry Retry counter
	 */
	_ensureReplication(retry) {
		// Don't re-create a replicator that is not meant to be
		if (this._state === 'inactive' || this._state === 'cleanup' || !this._targetDB) {
			return
		}

		// Cancel current replicator if there is one
		this._cancelCurrentReplicator()

		this._log.info('Attempting replication', {retry})

		// (re)create replicator and listen to events for state magic
		this._updateState('connecting')
		this._replication = this._createReplicator()
			.on('paused', err => {
				if (!err) {
					this._backoff.success()
				}
				this._updateState('pause', err)
			})
			.on('active', () => {
				this._backoff.success()
				this._updateState('active')
			})
			.on('denied', err => {
				// This is not fatal for the replicator but is a pretty bad sign
				this._log.error('Replication denied', err)
			})
			.once('error', err => {
				// This instance is bad, discard and recreate it with exponential backoff
				this._log.warn('Replication error', err)
				this._backoff.backoff()
			})
			.once('complete', () => {
				// This instance is bad, discard and recreate it with exponential backoff
				this._log.warn('Replication canceled/completed')
				this._backoff.backoff()
			})
	}

	/**
	 * Creates a new PouchDB replicator.
	 * Remember that this class is simply a wrapper.
	 * @param {Object} [opts] PouchDB replication options.
	 * @param {Boolean} [opts.live=true] Keep the replication running, pushing changes as they happen.
	 * @param {Boolean} [opts.retry=false] Enable PouchDB's built-in backoff algorithm.
	 * @returns {PouchDBReplicationID}
	 */
	_createReplicator(opts = {live: true, retry: false, batch_size: 20, batch_limit: 5}) { // eslint-disable-line camelcase
		return this._sourceDB.replicate.to(
			this._targetDB,
			opts
		)
	}

	/**
	 * Updates the Replicator's {@link Replicator#_state}.
	 * @param {String} state New state.
	 * @param {Object} [moreLogData] Extra data to be included in the log.
	 * @emits state(state)
	 */
	_updateState(state, moreLogData) {
		// Prevent infinite loops of the log replicator flipping between active and inactive
		// Just log active/paused states when you're not switching between them or when errors occur
		if ((state !== 'active' && state !== 'pause') || (this._state !== 'active' && this._state !== 'pause') || moreLogData) {
			this._log.info('updateState', moreLogData, {state})
		}
		this._state = state
		this.emit('state', state)
	}
}

import {EventEmitter} from 'events'
import {get} from 'object-path'

import packetHeuristicsConfig from './packet-heuristics'

/**
 * Calculates packet and station scores.
 * Not an AI classifier.
 * Packet scores are calculated based on the CRC checksum(60%)
 * and simple heuristics(min/max value, variation between packets)(40%).
 * The heuristics used are configured in the packet-heuristics.js map.
 * Station scores are calculated based on the average packet score. The weight of
 * each packet score is determined by the racional function 1/2x where x is a
 * positive non-zero integer that represents how recent the packet is (1 being the
 * most recent packet).
 */
export default class Classifier extends EventEmitter {
	/**
	 * Constructor.
	 * @param {Bunyan} logger Logger instance.
	 */
	constructor(logger) {
		super()

		/**
		 * The station score.
		 * @type {!Number}
		 */
		this.stationScore = undefined

		/**
		 * The last data received (all packets merge in here).
		 * Used for heuristic algorithms.
		 * @type {Object}
		 */
		this._lastData = Object.create(null)

		/**
		 * Logger instance.
		 * @type {Bunyan}
		 */
		this._log = logger

		this._log.info('classifier.construct')
		this._log.debug('classifier config', {packetHeuristicsConfig})
	}

	/**
	 * Classifies one packet and updates the station score (unless told otherwise).
	 * Packet scores are calculated based on the CRC checksum(60%)
	 * and simple heuristics(min/max value, variation between packets)(40%).
	 * @param {Object} packet The packet to classify.
	 * @param {Boolean} [updateStationClassification=true] Whether to update the station score.
	 * @emits stationScore(stationScore): when updateStationClassification is true, the station score is updated and the event is fired.
	 * @returns {Number} Packet score.
	 */
	classifyPacket(packet, updateStationClassification = true) {
		this._log.info('classifier.classifyPacket', {updateStationClassification})
		this._log.debug('classifier.classifyPacket', {packet})
		let score = 0

		// Iterate and run each heuristic
		let heuristicCount = 0
		for (const [fields, heuristics] of packetHeuristicsConfig) {
			for (const field of fields) {
				// Get field current value
				const val = get(packet, field)

				// When there is no value, the field does not exist => wrong kind of packet.
				if (val === undefined) {
					this._log.debug('no value, skipping heuristics for', field, {val})
					continue
				}

				// Get last values
				const lastVal = get(this._lastData, field, val)

				for (const heuristic of heuristics) {
					heuristicCount++
					this._log.trace('Running heuristic', heuristic.name)
					score += heuristic(val, lastVal)
				}
			}
		}

		// Bump score to a 0-40 range
		score *= 40 / heuristicCount

		// The CRC accounts for 60% of the score, unless there are no heuristics
		if (packet.crc.sent === packet.crc.local) {
			if (score === Infinity || score === -Infinity || isNaN(score)) {
				score = 100
			} else {
				score += 60
			}
		} else if (score === Infinity || score === -Infinity || isNaN(score)) {
			score = 0
		}

		// Update station classification
		if (updateStationClassification) {
			this.classifyStationInc(score)
		}

		// Update _lastData with the current packet fields
		Object.assign(this._lastData, packet)

		return score
	}

	/**
	 * Updates the station score(incrementally).
	 * Station scores are calculated based on the average packet score. The weight of
	 * each packet score is determined by the racional function 1/2x where x is a
	 * positive non-zero integer that represents how recent the packet is (1 being the
	 * most recent packet).
	 * @param {Number} packetScore The score of the previously unnacounted packet.
	 * @returns {Number} New station score.
	 * @emits stationScore(stationScore): because the station score was updated.
	 */
	classifyStationInc(packetScore) {
		if (this.stationScore === undefined) {
			this.stationScore = packetScore
		} else {
			this.stationScore += packetScore
			this.stationScore /= 2
		}

		// Warn about the change
		this.emit('stationScore', this.stationScore)

		return this.stationScore
	}
}

import {v4 as uuid} from 'uuid'

import IcarusParser from './parser'

/**
 * 'Transceiver MODE' Buffer for future comparison.
 * Helps ignoring messages from transceiver.
 * @private
 * @type {Buffer}
 */
// TODO: check with the CanSat on: is this what we receive?
const tmBuffer = Buffer.from('Transceiver MODE')

/**
 * Creates and returns a "byteDelimiter" parser.
 * DISCLAIMER: It's a patched version of node-serialport's byteDelimiter parser.
 * You can find it's source here: https://github.com/EmergingTechnologyAdvisors/node-serialport/tree/master/lib/parsers.js
 * Changes from the original: trailing delimiters are removed, buffer passes through packetParser.parse before being emitted
 * and empty buffers and 'Transceiver MODE' messages are ignored.
 * Separates packets by a byte delimiter and passes them through {@link packetParser}
 * @returns {Function} The parser itself
 */
/*
