AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING
FROM, OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS
IN THE SOFTWARE.
*/
export function parser() {
	const delimiter = [13, 10] // \r\n
	let buf = []
	let nextDelimIndex = 0

	return (emitter, buffer) => {
		for (let i = 0; i < buffer.length; i++) {
			buf[buf.length] = buffer[i]

			if (buf[buf.length - 1] === delimiter[nextDelimIndex]) {
				nextDelimIndex++
			}

			if (nextDelimIndex === delimiter.length) {
				// Remove trailing 254, 255 and ignore empty packets/'Transceiver MODE'
				// Spare the parser the trouble, packets are always at least 6 bytes
				if (buf.length >= 8 && !Buffer.from(buf).equals(tmBuffer)) {
					// Create one packetParser object per packet
					// If this one crashes, the others have a chance.
					// It shouldn't throw any exception, but sometimes weird things happen.
					// Let us not forget Murphy's Law.
					const packetParser = new IcarusParser()

					emitter.emit('data', packetParser.parse(Buffer.from(buf.slice(0, buf.length - 2))))
				}

				buf = []
				nextDelimIndex = 0
			}
		}
	}
}

/**
 * A handler for incoming {@link Serial#data}.
 * Saves packets to the local database.
 * Should be called bound to a {@link Station}.
 * @param {Object} packet Packet to be parsed and saved.
 * @return {Promise} Resolves when all is done.
 */
export function dataHandler(packet) {
	// Properly handle bad packets
	if (packet.type.length === 1 && packet.type !== '?') {
		// Assign packet score
		packet.score = this.classifier.classifyPacket(packet)
	} else {
		// Bad packets have score 0
		packet.score = 0
		this.classifier.classifyStationInc(0)

		// Generate a mostly random id
		packet._id = `?${packet.receivedAt}-${uuid()}`
	}

	// Alert UI through station
	this.emit('packet', packet)

	// Save to DB
	return this.db.put(packet)
		.then(() => this._log.trace('packet saved', {packet}))
		.catch(err => this._log.error(err))
}

import Parser from '../../parser'
import * as conv from './unit-conv'
import {messages, moduleNames} from './cansat-strings' // eslint-disable-line import/named

/**
 * The parser for Team Icarus' CanSat.
 */
export default class IcarusParser extends Parser {
	/**
	 * Implementation of {@link Parser#parse}.
	 * Automatically decodes the quasi-binary packet with {@link decode}.
	 * Supports telemetry, info and settings packets.
	 * Also converts sensor values to standard units and attaches text representations
	 * of module names and messages.
	 * @param {Buffer} rawPacket Packet to parse.
	 * @returns {Object} Parsed packet.
	 */
	parse(rawPacket) {
		// Decode packet
		this._raw = Buffer.from(rawPacket.toString(), 'base64')

		// Parser needs to cleanup some things
		super.parse(this._raw)

		// Save raw packet (encoded version)
		this.packet.raw = rawPacket.toJSON().data

		// Save receival time
		this.packet.receivedAt = Date.now()

		try {
			// Stop right now if there are no checksums
			if (!this.packet.crc) {
				throw new Error('Missing CRC checksums')
			}

			// Get packet type identifier
			this.readChar('type')

			// Only handle known packets
			if (this.packet.type === 't' || this.packet.type === 'i' || this.packet.type === 's') {
				// Now the packet counter, timestamps and alike
				this.readUInt('counter', 4)
				this.readUInt('sentAt.millis', 4)
				this.readUInt('sentAt.unix', 4) // Not yet as of now

				// Generate the packet ID
				this.packet._id = String(this.packet.counter)
			}

			switch (this.packet.type) {
				case 't':
					// Temperature
					this.readInt('temp.0', 2, conv.DS18B20)
					this.readInt('temp.1', 2, conv.DS18B20)

					// Pressure
					this.readInt('press.0', 2, conv.MPX4115A)
					this.readInt('press.1', 2, conv.MPX4115A)

					// GPS data
					this.readUInt('gps.flags', 1, conv.gpsFlags)

					// GPS Latitude
					this.readUInt('gps.lat.deg', 2)
					this.readUInt('gps.lat.billionths', 4)
					this.setValue('gps.lat', this.packet.gps.lat, conv.gpsCoordinate)
					if (this.packet.gps.flags.latSign) {
						// The sign bit is true = the value the negative
						this.packet.gps.lat *= -1
					}

					// GPS Longitude
					this.readUInt('gps.lng.deg', 2)
					this.readUInt('gps.lng.billionths', 4)
					this.setValue('gps.lng', this.packet.gps.lng, conv.gpsCoordinate)
					if (this.packet.gps.flags.lngSign) {
						// The sign bit is true = the value the negative
						this.packet.gps.lng *= -1
					}

					this.readInt('gps.speed', 4, conv.gpsSpeed)
					this.readInt('gps.course', 4, conv.gpsCourse)
					this.readInt('gps.altitude', 4, conv.gpsAltitude)

				// Acceleration
					this.readInt('accel.0.x', 2, conv.LIS331HH_24G)
					this.readInt('accel.0.y', 2, conv.LIS331HH_24G)
					this.readInt('accel.0.z', 2, conv.LIS331HH_24G)
					this.readInt('accel.1.x', 2, conv.MMA7361_6G)
					this.readInt('accel.1.y', 2, conv.MMA7361_6G)
					this.readInt('accel.1.z', 2, conv.MMA7361_6G)
					break
				case 'i':
					// Message code and interpretation
					this.readUInt('message.id')
					this.setValue('message.text', messages[this.packet.message.id] || 'Unknown message')
					break
				case 's':
					// Module information
					this.readUInt('module.id')
					this.readBoolean('module.enable')
					this.readUInt('module.interval', 4)
					this.readUInt('module.lastRun', 4)

					// Get module name
					this.setValue('module.name', moduleNames[this.packet.module.id] || 'Unknown module')
					break
				default:
					this.packet.type = `?[0x${this.packet.type.charCodeAt(0).toString(16)}]`
					break
			}
		} catch (err) {
			// When we can't decode it, we can't continue
			this.packet.error = err.message

			this.packet.type = '?[error]'
		}

		return this.packet
	}
}

/**
 * Gets one bit out of a byte/bytes.
 * @param b {Number} The byte or bytes (up to one 32bit unsigned integer).
 * @param i {Number} The bit index.
 * @return {Boolean} Bit value.
 */
const getBit = (b, i) => Boolean((b >> i) & 1)

export function analogToMV(analogReadResult) {
	// Raw values range from 0 to 1023, where 0 -> 0V and 1024 -> 5V (or 5000mV) (Arduino analogRead reference)
	return (analogReadResult * 5000) / 1023
}

/**
 * Converts raw DS18B20 temperatures to C.
 * @param {Number} raw Raw DS18B20 temperature.
 * @return {Number} C temperature.
 */
export function DS18B20(raw) {
	return raw / 128 // TODO: confirm value, that's what's done in the Arduino Library
}

/**
 * Converts raw MPX4115A temperatures to hPa.
 * @param {Number} raw Raw MPX4115A pressure.
 * @return {Number} hPa pressure.
 */
export function MPX4115A(raw) {
	let vOut = analogToMV((5000 * raw) / 1023)

	// 204mV -> offset for minimum rated pressure (Datasheet)
	vOut -= 204

	// 45.9 mV/kPa (Datasheet)
	// 15 kPa -> minimum rated pressure (Datasheet)
	// last multiplication by 10: conversion from kPa to hPa (1kPa = 10hPa)
	return ((vOut / 45.9) + 15) * 10
}

export function LIS331HH_24G(raw) {
	// 12 mG/digit (Datasheet)
	// mGs, not Gs, so we need to divide by 1000
	return (raw * 12) / 1000
}

export function MMA7361_6G(raw) {
	// 206 mV/G (Datasheet)
	// 0G  1.65V or 1650mV (Datasheet)
	return (analogToMV(raw) - 1650) / 206
}

/**
 * Processes the GPS boolean flags that are crammed in one byte.
 * In this order we have: latitude sign, longitude sign, location validity,
 * speed validity, course validity and altitude validity.
 * @param {Number} raw The number (0-255) containing the flags.
 * @return {Object} Flags as an object.
 */
export function gpsFlags(raw) {
	return {
		latSign: getBit(raw, 0),
		lngSign: getBit(raw, 1),
		locationValid: getBit(raw, 2),
		speedValid: getBit(raw, 3),
		courseValid: getBit(raw, 4),
		altitudeValid: getBit(raw, 5)
	}
}

/**
 * Convert raw coordinate object ({deg, billionths}) into an actual latitude
 * or longitude coordinate.
 * This does not check any sign/cardinal orientation. The coordinate is always
 * positive.
 * @param {Object} raw Raw coordinate as {deg, billionths}.
 * @return {Number} The GPS coordinate.
 */
export function gpsCoordinate(raw) {
	// As it's done in TinyGPS++
	return raw.deg + (raw.billionths / 1000000000.0)
}

export function gpsSpeed(raw) {
	// As it's done in TinyGPS++
	return (raw * 0.51444444) / 100.0
}

export function gpsCourse(raw) {
	// As it's done in TinyGPS++
	return raw / 100.0
}

export function gpsAltitude(raw) {
	// As it's done in TinyGPS++
	return raw / 100.0
}

import {EventEmitter} from 'events'

import Serial, {listPorts} from '../serial'
import getDB from '../lib/db'
import createLogger from '../lib/log'
import Backend from './backend'
import Classifier from './classifier'
import {parser, dataHandler} from './data-handler'

// T-Minus Transceiver information
/**
 * T-Minus Transceiver Vendor ID
 * @const
 */
const tMinusVid = '0x03eb'

/**
 * T-Minus Transceiver Product ID
 * @const
 */
const tMinusPid = '0x2404'

/**
 * Handles everything a Station should.
 * Brings Serial, data parsing, database saving and the backend connection together.
 */
export default class Station extends EventEmitter {
	/**
	 * Sets up all relevant class instances (Serial, parsers...) and events listeners.
	 * @param {String} name Station name.
	 */
	constructor(name) {
		super()

		/**
		 * Name/ID of the station
		 * @type {String}
		 */
		this.name = name

		/**
		 * Database instance, internal to the station.
		 * @type {PouchDB}
		 */
		this.db = getDB(`data-${name}`)

		/**
		 * Log database instance, internal to the station.
		 * @type {PouchDB}
		 */
		this.logDB = getDB(`log-${name}`)

		/**
		 * Logger instance.
		 * @type {Bunyan}
		 */
		this._log = createLogger(this.name, this.logDB)

		/**
		 * {@link Serial} instance with the {@link parser} attached.
		 * @type {Serial}
		 */
		this.serial = new Serial(this._log.child({childId: 'serial'}), parser())

		/**
		 * {@link Classifier} instance.
		 * @type {Classifier}
		 */
		this.classifier = new Classifier(this._log.child({childId: 'classifier'}))

		/**
		 * {@link Backend} connector instance.
		 * @type {Backend}
		 */
		this.backend = new Backend(
			this.name,
			this._log.child({childId: 'backendLink'}),
			this.db,
			this.logDB
		)

		// Handle incoming packets
		this.serial.on('data', this::dataHandler)

		// The transceiver needs a bit of a kick sometimes
		this.serial.on('stateChange', state => {
			if (state === 'open') {
				// Send something!
				this.serial._port.write('hi there')
			}
		})

		this._log.info('station.construct end')
	}

	/**
	 * Returns a list of available serialports and tries to find which ones
	 * are the T-Minus transceiver (sets [portInfo].recommend to true) and sorts
	 * them with recommended ones first.
	 * @return {Promise<Array>} List of serialports.
	 */
	getAvailablePorts() {
		this._log.info('station.getAvailablePorts')
		return listPorts()
			.then(list => {
				list = list.map(port => {
					if (port.vendorId === tMinusVid && port.productId === tMinusPid) {
						port.recommend = true
					}
					return port
				})

				list = list.sort((p1, p2) => {
					// Recommended come first
					if (p1.recommend && !p2.recommend) {
						return -1
					}
					if (p2.recommend && !p1.recommend) {
						return 1
					}

					// Otherwise, default to alphabetical sorting
					return p1.comName.localeCompare(p2.comName)
				})

				this._log.debug('Available ports', {ports: list})
				return list
			})
	}

	/**
	 * Cleanup function to be run before exiting.
	 * Closes the serialport and runs {@link Backend#cleanup}.
	 * @return {Promise} Promise that completes when all is clean.
	 */
	cleanup() {
		this._log.info('station.cleanup')
		return this.serial.close()
			.then(this.backend.cleanup())
	}
}

