/**
 * A wrapper over the ExponentialBackoffStrategy from the backoff module.
 */
export default class ExponentialBackoff extends EventEmitter {
	/**
	 * Constructor.
	 */
	constructor() {
		super()

		/**
		 * ExponentialBackoffStrategy instance.
		 * @type {ExponentialBackoffStrategy}
		 */
		this._strategy = new ExponentialBackoffStrategy({
			randomisationFactor: 0.3,
			initialDelay: 1000,
			maxDelay: 60000
		})

		/**
		 * Number of previous failed attempts.
		 * @type {Number}
		 */
		this._backoffNumber = 0

		/**
		 * Backoff timeout ID.
		 * @type {TimeoutID}
		 */
		this._timeoutID = undefined
	}

	/**
	 * Signals that we tried executing the protected routine and failed, or that we want to start the process.
	 * @emits backoff(_backoffNumber, delay) delay is how much time will pass before we try executing the routine.
	 */
	backoff() {
		// Backoff already in progress
		if (this._timeoutID) {
			return
		}

		const delay = this._strategy.next()
		this._timeoutID = setTimeout(() => {
			this._timeoutID = undefined
			this.emit('retry', this._backoffNumber)
			this._backoffNumber++
		}, delay)
		this.emit('backoff', this._backoffNumber, delay)
	}

	/**
	 * Resets failed attempt number and cancels a possible pending action (routine execution after backoff).
	 */
	reset() {
		// Reset number of backoffs
		this._backoffNumber = 0

		// Reset strategy
		this._strategy.reset()

		// Clear a possibly running timeout
		if (this._timeoutID !== undefined) {
			clearTimeout(this._timeoutID)
			this._timeoutID = undefined
		}
	}

	/**
	 * Used to signal the routine was successful.
	 * This resets the wrapper.
	 * @emits success
	 * @see {@link reset}
	 */
	success() {
		// Reset state
		this.reset()

		// Alert everyone we did it (for now at least)
		this.emit('success')
	}
}

import url from 'url'
import PouchDB from 'pouchdb'
import normalizePath from './path'

/**
 * Gets a local database.
 * @param {String} name The database name.
 * @returns {PouchDB} PouchDB instance referring to the requested database.
 */
export default function getDB(name) {
	return new PouchDB(normalizePath(name))
}

/**
 * Gets a remote database.
 * When provided a URL with no port, the default CouchDB ports for HTTP and HTTPS will be added automatically.
 * @param {String} name The database name or URL.
 * @param {String} username The database username.
 * @param {String} password The database password.
 * @returns {PouchDB} PouchDB instance referring to the requested database.
 */
export function getRemoteDB(name, username, password) {
	let dburl = name

	if (name.includes('//')) {
		const urlObj = url.parse(name)
		const port = urlObj.port || (urlObj.protocol === 'https:' ? 6984 : 5984)
		dburl = `${urlObj.protocol}//${urlObj.hostname}:${port}${urlObj.path}`
	}

	return new PouchDB(dburl, {
		auth: {
			username,
			password
		}
	})
}

import bunyan from 'bunyan'
import normalizePath from './path'

/**
 * Creates a configured bunyan logger.
 *
 * Our log output configuration:
 * - Everything goes to stdout
 * - debug-level or more severe messages go to a rotating file
 * - info-level or more severe messages go to the database
 *
 * When error-level or more severe messages are logged to the database,
 * they include an extra "context" property with the last 30 log messages
 * (of all levels).
 * @param {String} name Logger name.
 * @param {PouchDB} db Database where logs will be stored.
 * @returns {Bunyan}
 */
export default function createLogger(name, db) {
	const bufferStream = new bunyan.RingBuffer({limit: 30})
	const dbStream = {
		write: obj => {
			// Error and fatal items include more information
			if (obj.level >= 40) {
				// Sometimes, the current object may already be in the bufferStream
				// To prevent a cyclic dependency, let's filter it out
				const context = bufferStream.records.filter(record => record !== obj)

				obj.context = context
			}

			// Save to db
			db.post(obj)
		}
	}

	return bunyan.createLogger({
		name: `CanSatGS-${name}`,
		streams: [
			{
				level: 'trace',
				stream: process.stdout
			},
			{
				level: 'debug',
				type: 'rotating-file',
				path: normalizePath(`CanSatGS-${name}.log`),
				period: '3h', // New log file at every 3h
				count: 40 // Keep 40 log files
			},
			{
				level: 'info',
				type: 'raw',
				stream: dbStream
			},
			{
				level: 'trace',
				type: 'raw',
				stream: bufferStream
			}
		]
	})
}

/**
 * Forwards an event from one EventEmitter to another.
 * @param {EventEmitter} sourceEmitter EventEmitter to be listened.
 * @param {EventEmitter} destEmitter EventEmitter to where the event will be forwarded.
 * @param {String} sourceEvent Event name to be listened to.
 * @param {String} [destEvent=sourceEvent] Event name in the source
 */
export default function passEvent(sourceEmitter, destEmitter, sourceEvent, destEvent = sourceEvent) {
	sourceEmitter.on(sourceEvent, (...args) => destEmitter.emit(destEvent, ...args))
}

import {join} from 'path'

/**
 * A prefix for capture-lib files (DBs, logs).
 * Can be overriden via `global.pathPrefix` or `global.dbNamePrefix` (Deprecated).
 * It is used to store capture-lib files (like databases and logs)
 * in a specific directory that is not necessarily the current working directory.
 * @type {String}
 */
const pathPrefix = global.pathPrefix || global.dbNamePrefix || ''

export default function normalizePath(...path) {
	return join(pathPrefix, ...path)
}

import {crc32} from 'crc'
import {set as objPathSet} from 'object-path'

/**
 * Parser creation helper class. Any subclasses should reimplement the parse method,
 * calling super.parse in the beginning. You have useful methods for reading regular C
 * datatypes from the buffer: readInt, readFloat, readChar... To use it in node-serialport
 * or the serial wrapper just pass in ::Parser.parse
 * Keep in mind not initialize this class as is. RE-IMPLEMENT Parser.parse!
 */
export default class Parser {
	/**
	 * @param {String} [endianess='LE'] Set byte-order to big endian (BE) or little endian (LE).
	 */
	constructor(endianess = 'LE') {
		/**
		 * Current packet being parsed.
		 * @type {!Object}
		 * @protected
		 */
		this._raw = undefined

		/**
		 * Current position in the packet being parsed.
		 * @type {Number}
		 * @protected
		 */
		this._i = 0

		/**
		 * Parsed output container.
		 * @type {Object}
		 * @property {String} crc.sent The CRC32 checkum sent along with the packet.
		 * @property {String} crc.local The CRC32 checksum calculated from the sent data.
		 */
		this.packet = Object.create(null)

		/**
		 * Raw packet byte endianess (Little Endian (LE) for the Arduino).
		 * Acceptable values: LE, BE.
		 * @type {String}
		 */
		this.endianess = endianess
	}

	/**
	 * Parses a packet. Default implementation resets state and calculates and extracts the CRC32
	 * checksum before parsing the new packet.
	 * That's why, if you want any parsing at all to be performed, YOU MUST RE-IMPLEMENT THIS METHOD.
	 * Preferrably calling this implementation before any parsing(but after any decoding
	 * *wink* *wink* quasi-binary decoder).
	 * @param {Buffer} rawPacket The packet to be parsed.
	 * @return {Object} The parsed object.
	 */
	parse(rawPacket) {
		// Change the current packet
		this._raw = rawPacket

		// Reset state thingies
		this._i = 0
		this.packet = Object.create(null)

		// Calculate CRC
		if (this._raw.length > 4) {
			this.setValue('crc.sent', rawPacket[`readUInt${this.endianess}`](rawPacket.length - 4, 4).toString(16))
			this.setValue('crc.local', crc32(rawPacket.slice(0, rawPacket.length - 4)).toString(16))
		}

		// More parsing reserved to subclass
		return this.packet // Just to behave as the Docs say it will
	}

	/**
	 * The value setter called by read* helper functions.
	 * Uses object path dot notation in the key and can convert values in one line.
	 * @param {String} key The object path where the value will be inserted.
	 * @param {mixed} val Anything you want to put.
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {mixed} The provided value (val).
	 */
	setValue(key, val, converter = v => v) {
		// Set the value in this.packet.[key]
		objPathSet(this.packet, key, converter(val))
		return val
	}

	/**
	 * Reads a signed integer of 1-6 bytes.
	 * @param {String} key The dot notation path where the int goes to.
	 * @param {Number} [size=1] The size in bytes of the integer (1 to 6 bytes only).
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {Number} The read integer.
	 */
	readInt(key, size = 1, converter) {
		// Get the value and update the index
		const val = this._raw[`readInt${this.endianess}`](this._i, size)
		this._i += size

		return this.setValue(key, val, converter)
	}

	/**
	 * Reads an unsigned integer of 1-6 bytes.
	 * @param {String} key The dot notation path where the int goes to.
	 * @param {Number} [size=1] The size in bytes of the integer (1 to 6 bytes only).
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {Number} The read integer.
	 */
	readUInt(key, size = 1, converter) {
		// Get the value and update the index
		const val = this._raw[`readUInt${this.endianess}`](this._i, size)
		this._i += size

		return this.setValue(key, val, converter)
	}

	/**
	 * Reads a 32-bit float.
	 * @param {String} key The dot notation path where the float goes to.
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {Number} The read float.
	 */
	readFloat(key, converter) {
		// Get the value and update the index
		const val = this._raw[`readFloat${this.endianess}`](this._i)
		this._i += 4

		return this.setValue(key, val, converter)
	}

	/**
	 * Reads a char.
	 * @param {String} key The dot notation path where the char goes to.
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {String} The read char.
	 */
	readChar(key, converter) {
		return this.setValue(key, String.fromCharCode(this._raw[this._i++]), converter)
	}

	/**
	 * Reads a boolean.
	 * @param {String} key The dot notation path where the boolean goes to.
	 * @param {Function} [converter=identity] A function that converts the value.
	 * @return {Boolean} The read boolean.
	 */
	readBoolean(key, converter) {
		return this.setValue(key, Boolean(this._raw[this._i++]), converter)
	}
}

import {EventEmitter} from 'events'
import SerialPort from 'serialport'

/**
 * A node-serialport wrapper that keeps track of port state and allows from on-the-fly
 * port path/name changes (automatically attaches/detaches event listeners and keeps config).
 */
export default class Serial extends EventEmitter {
	/**
	 * Constructor: the place for setting the baud rate and parser.
	 * @param {Function} [parser=raw] A node-serialport parser.
	 * @param {Number} [baud=19200] Desired baud rate.
	 */
	constructor(logger, parser, baud = 19200) {
		super()

		/**
		 * Holds the current node-serialport instance.
		 * @type {!SerialPort}
		 */
		this._port = undefined

		/**
		 * Holds the desired serial baud rate.
		 * @type {Number}
		 */
		this._baud = baud

		/**
		 * Holds the parsing function.
		 * @type {!Function}
		 */
		this._parser = parser

		/**
		 * Holds the current port path.
		 * @type {!String}
		 */
		this._path = undefined

		/**
		 * Holds current state. Possible values: disconnect, open, close, disconnect_force.
		 * @type {String}
		 */
		this._state = 'close'

		/**
		 * Logger instance. (Bunyan API).
		 * @type Object
		 */
		this._log = logger
	}

	/**
	 * Sets a new path to all future serialport instances. If a port is already open,
	 * it is automatically closed and a new one is opened with the new path (keeps event listeners).
	 * @emits data(packet): New data arrived (after being parsed by Serial#_parser).
	 * @emits stateChange(state): Serial#_state has changed.
	 * @emits error(error): An error ocurred in node-serialport.
	 * @return {Promise} When path is changed and the port recreated/reopened.
	 */
	setPath(path) {
		this._log.debug('serial.setPath')
		// Guard for already changed port...
		if (path === this._path) {
			this._log.trace('serial._path needs no change', path)
			return Promise.resolve()
		}

		return (new Promise(resolve => {
			// Any _port recreation will open this new path
			this._log.trace('serial._path changed to', path)
			this._path = path

			// Destroy and recreate _port when necessary
			if (this._port) {
				this._log.trace('serial._path changed: recreating existing port')
				const open = this._port.isOpen()
				this._destroyPort()
					.then(() => open ? this.open() : this._createPort())
					.then(resolve)
			} else {
				resolve()
			}
		}))
			.then(() => this.emit('pathChange', path))
	}

	/**
	 * Opens the serialport, creating it if needed.
	 * @emits data(packet): New data arrived (after being parsed by Serial#_parser).
	 * @emits stateChange(state): Serial#_state has changed.
	 * @emits error(error): An error ocurred in node-serialport.
	 * @return {Promise} Resolved when the serial port is open.
	 */
	open() {
		this._log.debug('serial.open')
		return new Promise(resolve => {
			// No port? Create it!
			if (!this._port) {
				this._log.trace('port does not exist')
				return this._createPort()
					.then(() => this.open())
					.then(resolve)
			}

			// Already open = nothing to do
			if (this._port.isOpen()) {
				this._log.trace('port already open')
				return resolve()
			}

			// Open the port
			this._log.trace('calling serial._port.open')
			this._port.open(resolve)
		})
	}

	/**
	 * Closes the serialport.
	 * @emits stateChange(state): Serial#_state} has changed.
	 * @emits error(error): An error ocurred in node-serialport.
	 * @return {Promise} Resolved when the serialport is closed.
	 */
	close() {
		this._log.debug('serial.close')
		// Already closed/no port = nothing to do
		if (!this._port || !this._port.isOpen()) {
			this._log.trace('port already closed/does not exist')
			return Promise.resolve()
		}

		this._log.trace('calling serial._port.close')
		return new Promise(resolve => this._port.close(resolve))
	}

	/**
	 * Destroys the current serialport instance. Removing all listeners and closing it beforehand.
	 * @protected
	 * @emits stateChange(state): Serial#_state has changed.
	 * @returns {Promise} Resolves when all is done.
	 */
	_destroyPort() {
		this._log.debug('serial._destroyPort')
		if (!this._port) {
			return Promise.resolve()
		}

		return new Promise(resolve => {
			this._port.removeAllListeners()

			// Only close if necessary
			if (!this._port.isOpen()) {
				this._log.trace('port not open, removing it')
				this._port = undefined
				return resolve()
			}

			this._log.trace('port open, closing')
			this._port.close(error => {
				if (error) {
					this._log.error('Error closing port!', error)
				}

				// Must keep going
				this._port = undefined
				resolve()
			})
		})
	}

	/**
	 * Creates the serialport instance and attaches all relevant event listeners
	 * that forward data and errors and keep track of state.
	 * @protected
	 * @emits data(packet): New data arrived (after being parsed by Serial#_parser).
	 * @emits stateChange(state): Serial#_state has changed.
	 * @emits error(error): An error ocurred in node-serialport.
	 * @return {Promise} A resolved Promise for easy chaining in {@link Serial#setPath}.
	 */
	_createPort() {
		// Only create if it does not exist
		if (this._port) {
			return Promise.resolve()
		}

		// Create serialport instance
		this._port = new SerialPort(this._path, {
			baudRate: this._baud,
			parser: this._parser,
			autoOpen: false
		})

		// Register event listeners
		this._port.on('data', data => this.emit('data', data))
		this._port.on('error', error => {
			this._log.fatal(error)
			this.emit('error', error)
		})

		// State tracking
		this._port.on('open', () => this._updateState('open'))
		this._port.on('disconnect', () => this._updateState('disconnect_force'))
		this._port.on('close', () => {
			// Detect safe disconnections
			if (this._state === 'disconnect_force') {
				return this._updateState('disconnect')
			}
			this._updateState('close')
		})

		return Promise.resolve()
	}

	/**
	 * Shortcut for updating state that changes this._state and emits
	 * a stateChange event with one call.
	 * @protected
	 * @param {String} state New state.
	 * @emits stateChange(state): Serial#_state has changed.
	 */
	_updateState(state) {
		this._state = state
		this.emit('stateChange', state)
	}
}

/**
 * Lists available ports in the system with SerialPort.list.
 * Tries to fill out vendorId and productId fields in Windows from pnpId.
 * @return {Promise<Array>} Array of port information objects.
 */
export function listPorts() {
	return new Promise(resolve => {
		SerialPort.list((err, ports) => {
			if (err) {
				// TODO: reject promise with error, deal with it in Station#getAvailablePorts
				return resolve([])
			}

			// Fill out some missing fields if possible
			ports = ports.map(port => {
				if ((!port.vendorId || !port.productId) && port.pnpId) {
					port.vendorId = '0x' + /VID_([0-9,A-Z]*)&/.exec(port.pnpId)[1].toLowerCase()
					port.productId = '0x' + /PID_([0-9,A-Z]*)&/.exec(port.pnpId)[1].toLowerCase()
				}

				return port
			})

			resolve(ports)
		})
	})
}
